import React, { Component } from 'react'
import Time from './Time'
import "./Time.css"

class Timer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            timeMs: 0
        }

    }
/*
    reset = () => {
        if (!this.state.interval) {
            return
        }
        cancelAnimationFrame(this.state.interval);
        this.setState({
            timeMs: this.setState.timeMs = 0
        })
    }*/
    start = () => {
        if (this.state.interval) {
            return
        }
        setInterval(this.state.interval)
        const interval = setInterval(
            () => {
                this.setState({
                    
                    timeMs: this.state.timeMs + 1000
                })

            }, 1000);
        this.setState({
            interval: interval
        })
    }
    reset = () => {
        if (!this.state.interval) {
            return
        }
        const interval = setInterval(
            () => {
                this.setState({
                    timeMs: this.state.timeMs = 0
                })

            }, -1);
        clearInterval(this.state.interval)
        this.setState({
            interval: undefined
        })
    }
    render() {
        return <div className="time-container">
            <Time ms={this.state.timeMs} />
            <input className='button' type="button" value="Start"
                onClick={this.start} />
            <input className='button' type="button" value="Reset"
                onClick={this.reset} />
        </div>
    }
}

export default Timer
