import React, { Component } from 'react'
import Time from './Time'

class Timer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            timeMs: 0
        }

    }
    start = () => {
        this.state = {
            timeMs: 0
        }

        if (this.state.interval) {
            return
        }
        const interval = setInterval(
            () => {
                this.setState({
                    timeMs: this.state.timeMs + 1000
                })

            }, 1000);
        this.setState({
            interval: interval
        })
    }
    reset = () => {
        if (!this.state.interval) {
            return
        }
        cancelAnimationFrame(this.state.interval);
        this.setState({
            timeMs: this.setState.timeMs = 0
        })
    }
    render() {
        return <div>
            <Time ms={this.state.timeMs} />
            <input type="button" value="Start"
                onClick={this.start} />
            <input type="button" value="Reset"
                onClick={this.reset} />
        </div>
    }
}

export default Timer
